import{_ as s,v as t,b as p,R as a}from"./chunks/framework.68346ff7.js";const g=JSON.parse('{"title":"🌐 dev Tools Page","description":"开发中常用的工具网站","frontmatter":{"sticky":202309252229,"description":"开发中常用的工具网站","title":"🌐 dev Tools Page","readingTime":false,"tag":["Tool"],"sidebar":true,"top":1,"publish":true},"headers":[],"relativePath":"sop/tools/page.md","filePath":"sop/tools/page.md","lastUpdated":1695652721000}'),e={name:"sop/tools/page.md"},o=a('<h3 id="_1、随机图片" tabindex="-1">1、随机图片 <a class="header-anchor" href="#_1、随机图片" aria-label="Permalink to &quot;1、随机图片&quot;">​</a></h3><p>这是一个可以生产随机图片的网站，可以通过修改一些地图获取到一张制定规格或样式的图片</p><ul><li><p>获取一张正方形的图片【图一】 <a href="https://picsum.photos/200" target="_blank" rel="noreferrer">https://picsum.photos/200</a></p></li><li><p>获取一张长方形（长 300 宽 200）的照片【图二】 <a href="https://picsum.photos/300/200" target="_blank" rel="noreferrer">https://picsum.photos/300/200</a></p></li><li><p>获取一张灰度的图片【图三】<a href="https://picsum.photos/200/300?grayscale" target="_blank" rel="noreferrer">https://picsum.photos/200/300?grayscale</a></p></li><li><p>获取一张带模糊度的图片（灰度值为1-10）【图四】<a href="https://picsum.photos/200/300/?blur" target="_blank" rel="noreferrer">https://picsum.photos/200/300/?blur</a></p></li><li><p>获取一张灰色模糊的图片【图五】<a href="https://picsum.photos/id/870/200/300?grayscale&amp;blur=2" target="_blank" rel="noreferrer">https://picsum.photos/id/870/200/300?grayscale&amp;blur=2</a></p></li><li><p>获取一张指定格式的图片【图六】<a href="https://picsum.photos/200/300.jpg" target="_blank" rel="noreferrer">https://picsum.photos/200/300.jpg</a></p></li></ul><p><strong>提示：</strong></p><blockquote><p>1、当前随机图片网站需要进行外网访问</p><p>2、当图片因浏览器缓存原因无法变化时，添加random参数防止被缓存</p><div class="language-html"><button title="Copy Code" class="copy"></button><span class="lang">html</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">img</span><span style="color:#89DDFF;"> </span><span style="color:#C792EA;">src</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">https://picsum.photos/200/300?random=1</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">&gt;</span></span></code></pre></div></blockquote><div class="box"><div class="item"><img src="https://picsum.photos/200"><div class="description">正方形的图片</div></div><div class="item"><img src="https://picsum.photos/300/200"><div class="description">长方形的图片</div></div><div class="item"><img src="https://picsum.photos/300/200?grayscale"><div class="description">灰色图片</div></div><div class="item"><img src="https://picsum.photos/300/200?blur=5"><div class="description">带模糊度图片</div></div><div class="item"><img src="https://picsum.photos/300/200?grayscale&amp;blur=2"><div class="description">灰度模糊的图片</div></div><div class="item"><img src="https://picsum.photos/300/200.jpg"><div class="description">指定格式的图片(jpg)</div></div></div>',6),i=[o];function r(l,c,n,d,h,m){return t(),p("div",null,i)}const v=s(e,[["render",r]]);export{g as __pageData,v as default};
